class Book {
  String title = '';

  String imageUrl = '';
  String decrption='';
  String Author='';
  double price=0.0;
  bool selected=false;


  @override


  Book(this.title, this.imageUrl,this.decrption,this.Author,this.price);

  @override
  String toString() {
    return 'Book{title: $title, imageUrl: $imageUrl, decrption: $decrption, Author: $Author, price: $price, selected: $selected}';
  }
}

